import re'''\d : 任何数字\D : 不是数字\s : 任何 white space, 如 [\t\n\r\f\v]\S : 不是 white space\w : 任何大小写字母, 数字和 “” [a-zA-Z0-9]\W : 不是 \w\b : 空白字符 (只在某个字的开头或结尾)\B : 空白字符 (不在某个字的开头或结尾)\\ : 匹配 \. : 匹配任何字符 (除了 \n)^ : 匹配开头$ : 匹配结尾? : 前面的字符可有可无'''# \d : decimal digitprint(re.search(r"r\dn", "run r4n"))           # <_sre.SRE_Match object; span=(4, 7), match='r4n'># \D : any non-decimal digitprint(re.search(r"r\Dn", "run r4n"))           # <_sre.SRE_Match object; span=(0, 3), match='run'># \s : any white space [\t\n\r\f\v]print(re.search(r"r\sn", "r n r4n"))          # <_sre.SRE_Match object; span=(0, 3), match='r\nn'># \S : opposite to \s, any non-white spaceprint(re.search(r"r\Sn", "r\nn r4n"))          # <_sre.SRE_Match object; span=(4, 7), match='r4n'># \w : [a-zA-Z0-9_]print(re.search(r"r\wn", "r\nn r4n"))          # <_sre.SRE_Match object; span=(4, 7), match='r4n'># \W : opposite to \wprint(re.search(r"r\Wn", "r\nn r4n"))          # <_sre.SRE_Match object; span=(0, 3), match='r\nn'># \b : empty string (only at the start or end of the word)print(re.search(r"\bruns\b", "dog runs to cat"))    # <_sre.SRE_Match object; span=(4, 8), match='runs'># \B : empty string (but not at the start or end of a word)print(re.search(r"\B runs \B", "dog  runs  to cat"))  # <_sre.SRE_Match object; span=(8, 14), match=' runs '># \\ : match \print(re.search(r"runs\\", "runs\ to me"))     # <_sre.SRE_Match object; span=(0, 5), match='runs\\'># . : match anything (except \n)print(re.search(r"r.n", "r[ns to me"))         # <_sre.SRE_Match object; span=(0, 3), match='r[n'># ^ : match line beginningprint(re.search(r"^dog", "dog runs to cat"))   # <_sre.SRE_Match object; span=(0, 3), match='dog'>    # match line beginning  每一行flags=re.M或者flags=re.MULTILINEstring = """dog runs to cat.I run to dog."""print(re.search(r"^I", string))                 # Noneprint(re.search(r"^I", string, flags=re.MULTILINE))     # <_sre.SRE_Match object; span=(18, 19), match='I'># $ : match line endingprint(re.search(r"cat$", "dog runs to cat"))   # <_sre.SRE_Match object; span=(12, 15), match='cat'># ? : may or may not occurprint(re.search(r"Mon(day)?", "Monday"))       # <_sre.SRE_Match object; span=(0, 6), match='Monday'>print(re.search(r"Mon(day)?", "Mon"))          # <_sre.SRE_Match object; span=(0, 3), match='Mon'>