import re# regular expressionpattern1 = "cat"pattern2 = "bird"string = "cot cat cut cit"ptn = r"c[au]t"print(re.search(ptn, string))print(re.search(r"r[A-Z]n", "dog runs to cat"))     # Noneprint(re.search(r"r[a-z]n", "dog runs to cat"))     # <_sre.SRE_Match object; span=(4, 7), match='run'>print(re.search(r"r[0-9]n", "dog r2ns to cat"))     # <_sre.SRE_Match object; span=(4, 7), match='r2n'>print("------------------------")print(re.search(r"r[0-9a-z]n", "dog runs to cat"))print(re.search(r"r[0-9a-z]n", "dog r2ns to cat"))print(re.search(r"r[0-9a-zA-Z]n", "dog rAns to cat"))